import React, { useEffect, useState } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from '../components/CheckoutForm';

const CheckoutPage = () => {
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
    // Make sure to send cart items in the body
    fetch('http://localhost:5000/api/payment/create-checkout-session', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        cartItems: [
          { name: 'Item 1', price: 20, quantity: 1 },
          { name: 'Item 2', price: 30, quantity: 2 }
        ]
      }),
    })
    .then((response) => response.json())
    .then((data) => {
      console.log('Session created:', data);
      // Handle session creation response
    })
    .catch((error) => console.error('Error:', error));
  }, []);


  const stripePromise = loadStripe("pk_test_51QUQ53Ep67J1jInyHXkyb9VliPuDAR5xGuB3S0WO5cXRI0SpV0hy4MM1Sg615l7trkUniec2oll2Xf8yJVnHy5CY00NHy45FNs");

  return (
    <div>
      <h1>Checkout</h1>
      {clientSecret && (
        <Elements stripe={stripePromise} options={{ clientSecret }}>
          <CheckoutForm />
        </Elements>
      )}
    </div>
  );
};

export default CheckoutPage;
