import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // Import Navigate for redirection
import Home from './pages/Home';
import ProductPage from './pages/ProductPage';
import About from './pages/About';
import Contact from './pages/Contact';
import Product from './components/Product';
import NavigationBar from './components/Navbar';
import CheckoutPage from './pages/CheckoutPage';
import CartPage from './pages/CartPage';
import 'bootstrap/dist/css/bootstrap.min.css';
import Success from './pages/Success';
import Cancel from './pages/Cancel';
import { AuthProvider, useAuth } from './context/AuthContext'; // Import useAuth hook
import Login from './pages/Login';
import AdminDashboard from './pages/AdminDashboard';
import UserDashboard from './pages/UserDashboard';

function App() {
  const [cart, setCart] = useState([]); // Cart state to store cart items

  const addToCart = (product, size) => {
    setCart([...cart, { ...product, size }]); // Add product to cart
  };

  return (
    <AuthProvider>
      <Router>
        <NavigationBar cart={cart} /> {/* Pass cart to NavigationBar */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/products/:id" element={<ProductPage addToCart={addToCart} />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/product/:id" element={<Product />} />
          <Route path="/cart" element={<CartPage cart={cart} setCart={setCart} />} />
          <Route path="/checkout" element={<CheckoutPage />} />
          <Route path="/success" element={<Success />} />
          <Route path="/cancel" element={<Cancel />} />
          <Route path="/login" element={<Login />} />

          {/* Protected Routes */}
          <Route
            path="/admin/dashboard"
            element={<ProtectedRoute role="admin" element={<AdminDashboard />} />}
          />
          <Route
            path="/user/dashboard"
            element={<ProtectedRoute role="user" element={<UserDashboard />} />}
          />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

const ProtectedRoute = ({ element, role }) => {
  const { user } = useAuth(); // Access the user state from AuthContext

  // Check if user is logged in and has the correct role (admin/user)
  if (!user) {
    // Redirect to login page if not logged in
    return <Navigate to="/login" />;
  }

  if (user.role !== role) {
    // Redirect to home page if user doesn't have the correct role
    return <Navigate to="/" />;
  }

  return element; // Allow access to the protected page if logged in and has correct role
};

export default App;
