import React from 'react';
import Cart from '../components/Cart';

function CartPage({ cart, setCart }) {
  return (
    <div className="cart-page">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <Cart cart={cart} setCart={setCart} />
      )}
    </div>
  );
}

export default CartPage;
