// src/components/Cart.js
import React from 'react';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('pk_test_51QUQ53Ep67J1jInyHXkyb9VliPuDAR5xGuB3S0WO5cXRI0SpV0hy4MM1Sg615l7trkUniec2oll2Xf8yJVnHy5CY00NHy45FNs'); // Replace with your Stripe key

function Cart({ cart }) {
  // Function to handle checkout
  const handleCheckout = async () => {
    try {
      // Make an API call to your backend to create a checkout session
      const response = await fetch('http://localhost:5000/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          cartItems: cart, // Send the cart items to the backend
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to create checkout session');
      }

      const session = await response.json();

      // Redirect to Stripe Checkout
      const stripe = await stripePromise;
      const { error } = await stripe.redirectToCheckout({
        sessionId: session.sessionId,
      });

      if (error) {
        console.error('Error redirecting to checkout:', error);
        alert('Error during checkout. Please try again.');
      }
    } catch (error) {
      console.error('Error in handleCheckout:', error);
      alert('Error occurred. Please try again.');
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {/* Display cart items */}
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        cart.map(item => (
          <div key={item.id}>
            <p>{item.name} - ${item.price}</p>
          </div>
        ))
      )}

      <button onClick={handleCheckout}>Proceed to Checkout</button>
    </div>
  );
}

export default Cart;
