// src/components/Cart.js (or wherever you want the cart checkout to be handled)

import React from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('your_stripe_publishable_key'); // Use your publishable key here

function Cart({ cart }) {
  const navigate = useNavigate();

  // Function to handle checkout
  const handleCheckout = async () => {
    // Make an API call to your backend to create a checkout session
    const response = await fetch('http://localhost:5000/create-checkout-session', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        cartItems: cart, // Send the cart items to the backend
      }),
    });

    const session = await response.json();

    // Redirect to Stripe Checkout
    const stripe = await stripePromise;
    const { error } = await stripe.redirectToCheckout({
      sessionId: session.sessionId,
    });

    if (error) {
      console.error('Error redirecting to checkout:', error);
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {/* Display cart items */}
      {cart.map(item => (
        <div key={item.id}>
          <p>{item.name} - ${item.price}</p>
        </div>
      ))}

      <button onClick={handleCheckout}>Proceed to Checkout</button>
    </div>
  );
}

export default Cart;
