import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home';
import ProductPage from './pages/ProductPage';
import About from './pages/About';
import Contact from './pages/Contact';
import NavigationBar from './components/Navbar';
import CartPage from './pages/CartPage'; // Ensure correct path
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [cart, setCart] = useState([]); // Cart state to store cart items

  const addToCart = (product, size) => {
    setCart([...cart, { ...product, size }]); // Add product to cart
  };

  return (
    <Router>
      <NavigationBar cart={cart} /> {/* Pass cart to NavigationBar */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/product/:id" element={<ProductPage addToCart={addToCart} />} /> {/* Use a single route for ProductPage */}
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/cart" element={<CartPage cart={cart} />} /> {/* Pass cart to CartPage */}
      </Routes>
    </Router>
  );
}

export default App;
