import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';

const stripePromise = loadStripe('pk_test_51QUQ53Ep67J1jInyHXkyb9VliPuDAR5xGuB3S0WO5cXRI0SpV0hy4MM1Sg615l7trkUniec2oll2Xf8yJVnHy5CY00NHy45FNs'); // Replace with your Stripe publishable key

function CheckoutPage() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const items = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(items);
  }, []);

  const handlePlaceOrder = async () => {
    // Assuming you have an API endpoint to create the checkout session
    const response = await fetch('/create-checkout-session', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cartItems }),
    });

    const session = await response.json();

    // Redirect to Stripe checkout
    const stripe = await stripePromise;
    const { error } = await stripe.redirectToCheckout({ sessionId: session.id });

    if (error) {
      console.error("Error redirecting to checkout: ", error);
    }
  };

  return (
    <div className="checkout-page">
      <h1>Checkout</h1>
      {cartItems && cartItems.length > 0 ? (
        cartItems.map(item => (
          <div key={item.id} className="cart-item">
            <p>{item.name}</p>
            <p>{item.price}</p>
            <p>Quantity: {item.quantity}</p>
            <p>Total: ${item.total}</p>
          </div>
        ))
      ) : (
        <p>Your cart is empty.</p>
      )}
      <button className="place-order" onClick={handlePlaceOrder}>Place Order</button>
    </div>
  );
}

export default CheckoutPage;
