// src/components/Cart.js
import React from 'react';

function Cart({ cart, setCart }) {
  // Calculate the total price of all items in the cart
  const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  // Function to handle item removal from the cart
  const handleRemove = (id) => {
    setCart(cart.filter(item => item.id !== id)); // Remove the item with the matching id
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {/* Display cart items */}
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.map(item => (
            <div key={item.id} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <img src={item.image} alt={item.name} style={{ width: '50px', marginRight: '10px' }} />
              <div style={{ flex: 1 }}>
                <p>{item.name} - ${item.price.toFixed(2)} x {item.quantity}</p>
              </div>
              <button onClick={() => handleRemove(item.id)} style={{ marginLeft: '10px' }}>Remove</button>
            </div>
          ))}
          <h3>Total: ${totalPrice.toFixed(2)}</h3>
          <button onClick={() => alert('Proceeding to checkout...')}>Proceed to Checkout</button>
        </div>
      )}
    </div>
  );
}

export default Cart;
