import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Card, Button, Row, Col, Container } from 'react-bootstrap';
import { FaCartPlus } from 'react-icons/fa'; // Importing the add to cart icon from React Icons

const Product = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch the list of products from the backend
    fetch('http://localhost:5000/api/products')  // Change URL as per your API route
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const handleAddToCart = (productId) => {
    // Logic for adding a product to the cart (you could use a context or state management for cart)
    console.log(`Product ${productId} added to cart`);
  };

  return (
    <Container>
      <h2>Our Products</h2>
      <Row>
        {products.map((product) => (
          <Col key={product._id} md={4} className="mb-4">
            <Card>
              <Card.Img variant="top" src={product.imageUrl} alt={product.name} />
              <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                <Card.Text>{product.description}</Card.Text>
                <Card.Text>${product.price}</Card.Text>
                <Button as={Link} to={`/products/${product._id}`} variant="primary">
                  View Details
                </Button>
                <Button 
                  variant="secondary" 
                  onClick={() => handleAddToCart(product._id)} 
                  className="mt-2"
                >
                  <FaCartPlus /> Add to Cart
                </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Product;
