import React from 'react';
import { useNavigate } from 'react-router-dom';

function CheckoutPage({ cart }) {
  const navigate = useNavigate();

  // Calculate total price
  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      const itemPrice = isNaN(item.price) ? 20 : item.price; // Default to $20 if price is NaN
      return total + itemPrice * item.quantity;
    }, 0);
  };

  const handlePlaceOrder = () => {
    // You can replace this with an actual order submission functionality
    alert('Order Placed Successfully!');
    navigate('/'); // Navigate back to home or any other page after placing the order
  };

  return (
    <div className="checkout-page">
      <h1>Checkout</h1>
      <h3>Order Summary</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Size</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={`${item.id}-${item.size}`}>
              <td>{item.name}</td>
              <td>{item.size}</td>
              <td>${(isNaN(item.price) ? 20 : item.price).toFixed(2)}</td>
              <td>{item.quantity}</td>
              <td>${(isNaN(item.price) ? 20 : item.price * item.quantity).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h4>Total: ${calculateTotal().toFixed(2)}</h4>

      <button className="place-order" onClick={handlePlaceOrder}>Place Order</button>
    </div>
  );
}

export default CheckoutPage;
