import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function Success() {
  const [sessionDetails, setSessionDetails] = useState(null);
  const { search } = useLocation();
  const sessionId = new URLSearchParams(search).get('session_id'); // Fixed the parentheses here

  useEffect(() => {
    // Fetch session details from your backend using the session ID
    if (sessionId) {
      axios
        .get(`http://localhost:5000/checkout-session/${sessionId}`)
        .then((response) => setSessionDetails(response.data))
        .catch((error) => {
          console.error('Error fetching session details:', error);
          alert('Failed to retrieve order details. Please try again later.');
        });
    }
  }, [sessionId]);

  if (!sessionId) {
    return <p>Invalid session. Please try again later.</p>;
  }

  return (
    <div>
      <h2>Payment Successful!</h2>
      <p>Thank you for your purchase!</p>
      {sessionDetails ? (
        <div>
          <h3>Order Details</h3>
          <p>Order ID: {sessionDetails.id}</p>
          <p>Total: ${(sessionDetails.amount_total / 100).toFixed(2)}</p>
          <p>Email: {sessionDetails.customer_email}</p>
        </div>
      ) : (
        <p>Loading order details...</p>
      )}
    </div>
  );
}

export default Success;
