import React from 'react';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('pk_test_51QUQ53Ep67J1jInyHXkyb9VliPuDAR5xGuB3S0WO5cXRI0SpV0hy4MM1Sg615l7trkUniec2oll2Xf8yJVnHy5CY00NHy45FNs'); // Replace with your Stripe public key

function Cart({ cart }) {
  // Log cart data for debugging
  console.log('Cart:', cart);

  // Function to calculate the total price of the cart
  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      const price = parseFloat(item.price) || 0; // Ensure price is a number
      const quantity = parseInt(item.quantity, 10) || 1; // Default quantity to 1 if missing
      return total + price * quantity;
    }, 0);
  };

  // Function to handle checkout
  const handleCheckout = async () => {
    try {
      // Make sure the cart is not empty
      if (cart.length === 0) {
        alert('Your cart is empty');
        return;
      }

      // Prepare cart items with quantity and image (assuming you store them)
      const cartItems = cart.map(item => ({
        name: item.name,
        price: parseFloat(item.price) * 100, // Stripe expects the price in cents
        quantity: parseInt(item.quantity, 10) || 1, // Default quantity to 1 if missing
        image: item.image || 'default-image-url', // Add a default image URL if none exists
      }));

      // Make an API call to your backend to create a checkout session
      const response = await fetch('http://localhost:5000/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          products: cartItems, // Send the cart items to the backend
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to create checkout session');
      }

      const session = await response.json();

      // Redirect to Stripe Checkout
      const stripe = await stripePromise;
      const { error } = await stripe.redirectToCheckout({
        sessionId: session.id, // Make sure the backend sends session.id
      });

      if (error) {
        console.error('Error redirecting to checkout:', error);
        alert('Error during checkout. Please try again.');
      }
    } catch (error) {
      console.error('Error in handleCheckout:', error);
      alert('Error occurred. Please try again.');
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {/* Display cart items */}
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        cart.map(item => (
          <div key={item.id} style={{ marginBottom: '1em' }}>
            <p>
              {item.name} - ${item.price} x {item.quantity || 1}
            </p>
            <img
              src={item.image || 'default-image-url'}
              alt={item.name}
              style={{ width: '50px', marginRight: '10px' }}
            />
          </div>
        ))
      )}

      {/* Display total price */}
      <h3>Total: ${calculateTotal().toFixed(2)}</h3>

      <button onClick={handleCheckout} disabled={cart.length === 0}>
        Proceed to Checkout
      </button>
    </div>
  );
}

export default Cart;
