import React from 'react';
import { Button, Table } from 'react-bootstrap';

function Cart({ cart, setCart }) {
  const handleRemove = (id, size) => {
    const updatedCart = cart.filter(item => !(item.id === id && item.size === size));
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const handleQuantityChange = (id, size, quantity) => {
    const updatedCart = cart.map(item =>
      item.id === id && item.size === size
        ? { ...item, quantity }
        : item
    );
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>Image</th>
          <th>Name</th>
          <th>Size</th>
          <th>Price</th>
          <th>Quantity</th>
          <th>Total</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {cart.map(item => (
          <tr key={`${item.id}-${item.size}`}>
            <td><img src={item.image} alt={item.name} style={{ width: '50px' }} /></td>
            <td>{item.name}</td>
            <td>{item.size}</td>
            <td>${item.price.toFixed(2)}</td>
            <td>
              <input
                type="number"
                value={item.quantity}
                min="1"
                onChange={(e) => handleQuantityChange(item.id, item.size, parseInt(e.target.value))}
              />
            </td>
            <td>${(item.price * item.quantity).toFixed(2)}</td>
            <td>
              <Button variant="danger" onClick={() => handleRemove(item.id, item.size)}>Remove</Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

export default Cart;
