// src/components/Product.js
import React from 'react';
import { useCart } from '../context/CartContext';  // Assuming you have CartContext.js for managing the cart

const Product = ({ product }) => {
  // Extracting addToCart function from CartContext
  const { addToCart } = useCart();

  // Handle add to cart functionality
  const handleAddToCart = () => {
    addToCart(product);
  };

  return (
    <div className="product-card" style={styles.productCard}>
      <img src={product.imageUrl} alt={product.name} style={styles.image} />
      <div className="product-details" style={styles.details}>
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <span>${product.price}</span>
      </div>
      <button onClick={handleAddToCart} style={styles.button}>
        Add to Cart
      </button>
    </div>
  );
};

// Styles for the product card
const styles = {
  productCard: {
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '16px',
    width: '250px',
    textAlign: 'center',
    margin: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
  },
  image: {
    width: '100%',
    height: 'auto',
    borderRadius: '8px',
  },
  details: {
    marginTop: '10px',
  },
  button: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '10px 16px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '10px',
    fontSize: '16px',
  },
};

export default Product;
