import React from 'react';
import { Navbar, Nav, Container, Badge } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useCart } from '../context/CartContext';
import { useUser } from '../context/UserContext';  // Import useUser

function NavigationBar() {
  const { cart } = useCart();
  const { user } = useUser();  // Access user data

  return (
    <Navbar expand="lg" sticky="top" className="custom-navbar">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand className="navbar-brand">Perfume Shop</Navbar.Brand>
        </LinkContainer>

        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto nav-links">
            <LinkContainer to="/">
              <Nav.Link className="nav-link">Home</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/about">
              <Nav.Link className="nav-link">About</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/contact">
              <Nav.Link className="nav-link">Contact</Nav.Link>
            </LinkContainer>

            {/* Cart Icon with Item Count */}
            <LinkContainer to="/cart">
              <Nav.Link className="nav-link">
                Cart
                {cart.length > 0 && (
                  <Badge pill bg="danger" className="ms-2">
                    {cart.length}
                  </Badge>
                )}
              </Nav.Link>
            </LinkContainer>

            {/* Display Login/Profile based on user state */}
            {user ? (
              <LinkContainer to="/profile">
                <Nav.Link className="nav-link">Profile</Nav.Link>
              </LinkContainer>
            ) : (
              <LinkContainer to="/login">
                <Nav.Link className="nav-link">Login</Nav.Link>
              </LinkContainer>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
