import React from 'react';
import { Navbar, Nav, Container, Badge } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useAuth } from '../context/AuthContext'; // Import useAuth hook

function NavigationBar() {
  const { user, logout } = useAuth(); // Get user data from context

  return (
    <Navbar expand="lg" sticky="top" className="custom-navbar">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand className="navbar-brand">Perfume Shop</Navbar.Brand>
        </LinkContainer>

        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto nav-links">
            <LinkContainer to="/">
              <Nav.Link className="nav-link">Home</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/about">
              <Nav.Link className="nav-link">About</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/contact">
              <Nav.Link className="nav-link">Contact</Nav.Link>
            </LinkContainer>

            {/* Show user's name if logged in */}
            {user ? (
              <>
                <span className="nav-link">Welcome, {user.name}</span>
                <LinkContainer to="/" onClick={logout}>
                  <Nav.Link className="nav-link">Logout</Nav.Link>
                </LinkContainer>
              </>
            ) : (
              <>
                <LinkContainer to="/login">
                  <Nav.Link className="nav-link">Login</Nav.Link>
                </LinkContainer>
                <LinkContainer to="/register">
                  <Nav.Link className="nav-link">Register</Nav.Link>
                </LinkContainer>
              </>
            )}

            {/* Cart Icon with Item Count */}
            <LinkContainer to="/cart">
              <Nav.Link className="nav-link">
                Cart 
                {/* Assuming you have a cart context */}
                {cart.length > 0 && (
                  <Badge pill bg="danger" className="ms-2">
                    {cart.length}
                  </Badge>
                )}
              </Nav.Link>
            </LinkContainer>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
