import React, { useEffect, useState } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from '../components/CheckoutForm';

const CheckoutPage = () => {
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
    // Call the backend to create a checkout session and get the client secret
    fetch('/api/payment/create-checkout-session', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ cartItems: [{ name: 'Item', price: 20, quantity: 1 }] }),
    })
      .then((response) => response.json())
      .then((data) => setClientSecret(data.id))
      .catch((error) => console.error('Error:', error));
  }, []);

  const stripePromise = loadStripe('pk_test_51QUQ53Ep67J1jInyHXkyb9VliPuDAR5xGuB3S0WO5cXRI0SpV0hy4MM1Sg615l7trkUniec2oll2Xf8yJVnHy5CY00NHy45FNs');

  return (
    <div>
      <h1>Checkout</h1>
      {clientSecret && (
        <Elements stripe={stripePromise} options={{ clientSecret }}>
          <CheckoutForm />
        </Elements>
      )}
    </div>
  );
};

export default CheckoutPage;
