// src/components/NavigationBar.js
import React from 'react';
import { Navbar, Nav, Container, Badge } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useCart } from '../context/CartContext'; // Import CartContext
import { useUser } from '../context/UserContext'; // Import UserContext
import './Navbar.css';

function NavigationBar() {
  const { cart } = useCart();  // Use CartContext to access the cart
  const { user, logout } = useUser(); // Use UserContext to access user state

  return (
    <Navbar expand="lg" sticky="top" className="custom-navbar">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand className="navbar-brand">Perfume Shop</Navbar.Brand>
        </LinkContainer>

        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto nav-links">
            <LinkContainer to="/">
              <Nav.Link className="nav-link">Home</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/about">
              <Nav.Link className="nav-link">About</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/contact">
              <Nav.Link className="nav-link">Contact</Nav.Link>
            </LinkContainer>

            {/* Cart Icon with Item Count */}
            <LinkContainer to="/cart">
              <Nav.Link className="nav-link">
                Cart 
                {cart.length > 0 && (  // Only display cart count if there are items
                  <Badge pill bg="danger" className="ms-2">
                    {cart.length}
                  </Badge>
                )}
              </Nav.Link>
            </LinkContainer>

            {/* Login/Register or Profile depending on user state */}
            {!user ? (
              <>
                <LinkContainer to="/login">
                  <Nav.Link className="nav-link">Login</Nav.Link>
                </LinkContainer>
                <LinkContainer to="/register">
                  <Nav.Link className="nav-link">Register</Nav.Link>
                </LinkContainer>
              </>
            ) : (
              <>
                <LinkContainer to="/profile">
                  <Nav.Link className="nav-link">Profile</Nav.Link>
                </LinkContainer>
                <Nav.Link className="nav-link" onClick={logout}>Logout</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
